import React, { useState } from "react";
import Item from "./ExpenseItem";
import "./expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "../NewExpenses/ExpenseFilter";
//import ExpensesFilter from "../NewExpenses/ExpenseFilter";

function Expense(props) {
  const [filteryear, setfilteredyear] = useState("");
  const filterchange = (SelectedYear) => {
    setfilteredyear(SelectedYear);
  };

  const filteredexpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteryear;
  });

  let expenseContent=<p>No expenses found!!</p>;
  if(filteredexpenses.length>0){
    expenseContent=filteredexpenses.map((expenses)=>(
      <ExpenseItem
      key={expenses.id}
      title={expenses.title}
      amount={expenses.amount}
      date={expenses.date}
      />
    ))

  }
  return (
    <Card className="expenses">
      <ExpensesFilter selected={filteryear} onchangefilter={filterchange} />
      {filteredexpenses === 0 && <p>No Expenses found.</p>}
      {filteredexpenses.length === 0 ? (
        <p>No Expenses Found.</p>
      ) : (
        filteredexpenses.map((expense) => (
          <Item
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))
      )}
    </Card>
  );
}

export default Expense;
