  return (
    <div className="chart">
      {props.dataPoints.map((dp) => (
        <ChartBar 
        key={dp.label}
        value={dp.value} 
        maxValue={totalmax}
        label={dp.label} />
      ))}
    </div>
  );
};

export default Chart;
